export class DividerComponent extends LitElement {

  static get styles(){
    return[
      dividersStyles,
      generalStyles
    ]
  }

  render() {
    return html`
      <div class="grid-column-1">
        <divider-dashed>
          <hr class="dashed">
        </divider-dashed>
      </div>
      <br><br>
      <div class="grid-column-1">
        <divider-dotted>
          <hr class="dotted">
        </divider-dotted>
      </div>
      <br><br>
      <div class="grid-column-1">
        <divider-solid>
        <hr class="solid">
        </divider-solid>
      </div>
    `;
  }
}

customElements.define('dividers-lit', DividerComponent);